package ZERO_JUDGE;

import java.util.Scanner;

public class SimpleAddition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in = new Scanner(System.in);
		int a = in.nextInt();
		int b = in.nextInt();
		int result = a + b;
		System.out.println(result);
		in.close();
	}

}
